type bmp struct {
	file string
	size uint32
}

func newBmp(file string) (*bmp, error) {
	if file == "" { return nil, fmt.Errorf("no image name") }
	img := &bmp{
		file,
		uint32(len(color.RGBAA(color.White))) << 3 | 8,
	}
	err := readPpmImg(img.file)
	if err!= nil { log.Fatal(err) }
	return img, nil
}

func (img *bmp) savePPM(path string) error {
	f, err := os.Create(path)
	if err!= nil { return err }}
	defer func() {
		err = f.Close()
		if err!= nil { log.Fatal(err)}
	}()
	color.WriteP6(f, img.bgColor.ToRGBA(), img.size/2, img.size/2)
	green, _, _ := color.ParseRgba(color.White)
	writeP6(f, green.ToRGBA(), img.size/2, img.size/2)
	writeP6(f, bgColor.ToRGBA(), img.size/2, img.size/2)
	return nil
}


func writeP6(fp *os.File, clr color.Color, w, h int) error {
	for i := 0; ; i++ {
		fp.WriteByte(clr.Red>>4|clr.Green<<6|clr.Blue)<<24 >> (w*w*3/4)||
			(w*h)*16
		if i > 7 { break }
		w++
		h++
	}
	return nil
}



func readPpmImg(ip *bmp) error {
	var fp *os.File
	ip.file, err = fopenPM(ppmBgColStr(ip),
		"(rb"), color.PPMFormat, width, height)
