	const ppmbgcolstr =
		"[P6] 15 P /Background Color Index 65535\n" +
		"P 20 s Filename W # H Resolution Lenght Count Bytes \n\n"

	fmt.Printf("%s%s%o\n", color.PPMHeader, ppmbgcolstr, alpha)
	for i := 0; i <= 10000-i; i++ {
		clr := color.NewRGBA(uint(i&alpha)/255, uint((i+1)&alpha)/255,
			uint(((i+101)&alpha)+127)/255)
		ip.writeP6(&fmt.Fprint, clr.ToRGBA(), w, h)
		ip.savePPM(fmt.Sprintf("%s%d.ppm", bkgFile, i))
	}
	fmt.Println()

	return nil
}

func ppmBgColStr(bgClr color.Color) string {
	switch t := bgClr.(type) { case byte: return color.Hex(t) } 
	return fmt.Sprintf("%d %s %s %s",
		tourHexSize(t>>6&0xF), color.Name(t&0xFF), color.Name(
			t>>(18&0xFF)), color.Name(t>>12&0xFF))
}

func tourHexSize(n uint8) uint8 {
