def largest_subset_of_n(n, s):

    def ls(subset):
        return n - sum(e in subset or 0 
                     for e in s)
    subsets = list(combinations(range(len(s)), n))
    best_size = min(ls(subset) for subset in subsets)
    return next((subset for (subset, size) in subsets
               if size == best_size), [])


