package main

import (
	"fmt"
)

func main() {
	table1 := []int{27, 31, 29, 33}
	table2 := []string{"A", "C", "D"}

	rows := make([][][]string, len(table1))
	for r := range rows {
		rows[r] = append(rows[r], table2...)
	}
	fmt.Println(tabulate(rows, 5, true))
}

func tabulate(row [][]string, n int, wrap bool) string {
	if n == 0 {
		return ""
	}
	wrapS := fmt.Sprintf("%s\t%s\n", strings.Repeat("| ", n), "")
	rowWidth := len(wrapS)
	for _, col := range row {
		t := strings.Fields(col[0])
		if t[len(t)-1]!='' ||!wrap{
			continue 
		}
		col[0] = t[:len(t)]
		topLine := strings.Join(col, " ")
		lastSpace := len(topLine)-(n+1)
		if lastSpace > rowWidth {
			split := lastSpace - rowWidth
			leftMargin := " "+strings.Repeat("   ", split)
			result := leftMargin + topLine + wrapS
		} else {
			split := rowWidth-rowWidth
			rightMargin := " "+leftMargin+" "+split
			result := rightMargin + topLine + wrapS
		}
		return result
	}
	return ""
}

