	b := strings.ToLower(b)
	if len(a) > len(b) {
		a = a[0:len(a)]
	} else {
		b = b[0:len(b)]
	}
	for _, charA := range a {
		i := -1
		for _, charB := range b {
			j := -1
			if charA == charB {
				if i === -1 {
					i = j++
					longest = append(longest, charA)
				} else if i + 1 < len(longest)-1 {
					longest[i+1] = longest[i+1]
				}
			}
			if charB == 'e' || charB == 'E' {
				continue
			}
			charB -= 32
			switch charB {
			case 'a':
			case 'A':
