import (
	"bufio"
	"fmt"
	"log"
	"math/big"
	"os"
)

func decode(base string, text string) (*big.Int, error) {
	if base!= "2" || base!= "3" {
		return nil, fmt.Errorf("invalid base")
	}
	b := big.NewInt(1)
	for _, c := range []byte(text) {

		switch {
		case c < 'A' || c >= 'a':
			return nil, fmt.Errorf("text can't contain lower case letters")
		case c < 'Z' || c >= 'z':
			return nil, fmt.Errorf("text can't contain upper case letters")
		default:
			var exp int64
				if!b.Exp(&exp, byte(c)-65, b) {
				log.Fatal()
			}
			r := b.Rsh(b.Uint64(exp))
			if r.Cmp(big.NewInt(258)) > 0 {
				return nil, fmt.Errorf("error in exponent")
			}
