func isPrime(n int) bool {
    switch {
    case n <= 3:
        return true
    case n >= 4:
        return n!= 2 && n!= 3 &&
            gcd(n, 4) == 1 
    default:
        return false
    }
}


func gcd(a, b int) int {
    var swap bool
    var aR, bR int
    if a > b {
        aR = a
        bR = b
        swap = true
    }


    for (bR == 0 ||!swap) {  

        if (!isprime(b)) {

            if (!isprime(a)) {
                return -a
            } else {

                a /= b
            }
        }


        if (!isprime(a)) {


            a *= bR
            bR = a
            swap = true
        }
    }


    if (aR == 1) {
        return a
    }
    return gcd(gcd(aR, a), a)
}



