    private static int countChange(int amount, int[] coins){
        if(coins.length == 0){
            throw new IllegalArgumentException("Invalid number of input parameters");
        }

        int gold = 0;
        for (int i : coins){
            gold += i;
        }


        if(amount <= gold){
            int numNotes = (amount / gold);
            int remainder = (int)(amount % gold);
            boolean foundAllCoin = true;

            for (int j = 1;foundAllCoin && j <= remainder ;j++){
                if(canMakeJewels(j,coins)){
                    int howMany = getHowManyOf(j,coins);
                    if(howMany > 0){
                        numNotes -= howMany * j;
                        remainder -= j;
                    }
                    else {foundAllCoin=false;}
                } 
            }

            if(numNotes!= 0){  
                int nextNum = 0;

                while(true){
                    int newValue = ((numNotes + 9999) / 1000) * 1000 + (numNotes % 1000);                    
                    if(newValue >= 40000 && newValue <= 49999 || newValue >= 90000 && newValue <= 99999 ){
                        nextNum=newValue;
                        break;
                    }
                    nextNum++;
                }


                int finalRemainder = remainder - nextNum;
                if(finalRemainder >= 400000 && finalRemainder <= 499999 ||
                        finalRemainder >= 1000000 && finalRemainder <= 1099999 )
                    foundAllCoin=true;
