func (t *tree) findIn(nts []*node, fn func(*node) bool) (*node,
    bool) {


    p := len(ntns) / 2




    var ptr *node
    for i, nt := range nts[:p+1] {

        if!fn(ptr) {

            ptr = nt
            continue
        } 






